sp_widget.client_script:
function($http) {
	/* widget controller */
	var b = this;

	//Using server.update()
	b.updateincwu= (function(){
		b.data.action = "updateinc";
		b.data.sd = "Updated by server.update";
		b.server.update().then(function (r) {
			b.data.action = undefined;
		});
	});

	//Using server.get
	// Use server.get to define a spesific input.
	b.updateincwg= (function(){
		var input = {
			"action":"updateinc",
			"sd":"Updated by server.get"
		};
		b.server.get(input).then(function (r) {
			b.data.action = undefined;
		});
	});

	//Using Rest 1
	b.updateincwr1= (function(){
		var data = {
			"short_description":"Updated using a REST call 1."
		};
		$http.put("/api/now/table/incident/57af7aec73d423002728660c4cf6a71c",data).then(function (r) {
		});
	});

	//Using Rest 2
	b.updateincwr2= (function(){
		var data = {
			"short_description":"Updated using a REST call 2."
		};
		$http({
			method:"PUT",
			url:"/api/now/table/incident/57af7aec73d423002728660c4cf6a71c",
			data:data,
			headers:{'Content-Type': 'application/json,charset=utf-8'}
		}).then(function (r) {
		});
	});

	//Using Service
	b.updateincws= (function(){
		b.data.action = "updateinc";
		b.server.update().then(function (r) {
			b.data.action = undefined;
			alert("Updated using a service.");
		});
	});

}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	if(input){
		if(input.action == "updateinc"){
			var grInc = new GlideRecord("incident");
			if(grInc.get("57af7aec73d423002728660c4cf6a71c")){
				grInc.setValue("short_description", input.sd);
				grInc.update();	
			}	
		}

	}
})();
